def search_in_files(directory, search_string, extensions):
    found_files = []
    for extension in extensions:
        pattern = os.path.join(directory, f'*.{extension}')
        files = glob.glob(pattern, recursive=True)
        for file in files:
            if extension == 'txt':
                result = search_in_text_file(file, search_string)
                if result:
                    found_files.extend(result)
            elif extension in ('xls', 'xlsx', 'xlsm'):
                result = search_in_excel_file(file, search_string)
                if result:
                    found_files.extend(result)
            elif extension == 'csv':
                result = search_in_csv_file(file, search_string)
                if result:
                    found_files.extend(result)
    return found_files

