def colors_red_to_red(text):
    gradient = [
        (255, 255, 255), (255, 200, 200), (255, 150, 150), (255, 100, 100), 
        (255, 50, 50), (255, 0, 0), (200, 0, 0), (150, 0, 0), (100, 0, 0), 
        (50, 0, 0), (0, 0, 0)
    ]
    lines = text.split('\n')
    gradient_step = len(gradient) // len(lines)
    colored_text = ''
    for i, line in enumerate(lines):
        if line.strip() == '':
            continue
        color_index = min(i * gradient_step, len(gradient) - 1)
        color = gradient[color_index]
        colored_text += f'\x1b[38;2;{color[0]};{color[1]};{color[2]}m{line}\x1b[0m\n'
    return colored_text

